import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination } from "swiper";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

const filmProfiles = [
  {
    name: "Linh Miêu: Quỷ Nhập Tràng (T18)",
    img: "https://example.com/linh-mieu.jpg",
    type: "2D",
    rating: "113",
    trailer: "https://example.com/trailer-linh-mieu",
    isShowing: true,
  },
  {
    name: "Cười Xuyên Biên Giới (T13) PD",
    img: "https://example.com/cuoi-xuyen-pd.jpg",
    type: "2D",
    rating: "113",
    trailer: "https://example.com/trailer-cuoi-xuyen",
    isShowing: true,
  },
  {
    name: "Cười Xuyên Biên Giới LT (T13)",
    img: "https://example.com/cuoi-xuyen-lt.jpg",
    type: "2D",
    rating: "113",
    trailer: "https://example.com/trailer-cuoi-xuyen-lt",
    isShowing: true,
  },
  {
    name: "Võ Sĩ Giác Đấu II (T18)",
    img: "https://example.com/vo-si.jpg",
    type: "2D",
    rating: "118",
    trailer: "https://example.com/trailer-vo-si",
    isShowing: true,
  },
];

const Slider = () => {
  return (
    <div className="bg-[#1C1C3A] py-8">
      <Swiper
        modules={[Navigation, Pagination]}
        navigation
        pagination={{ clickable: true }}
        spaceBetween={20}
        slidesPerView={3}
        breakpoints={{
          640: { slidesPerView: 1 },
          768: { slidesPerView: 2 },
          1024: { slidesPerView: 3 },
        }}
        className="container mx-auto"
      >
        {filmProfiles.map((film, index) => (
          <SwiperSlide key={index}>
            <div className="bg-white rounded-lg shadow-lg overflow-hidden mx-2">
              <img
                src={film.img}
                alt={film.name}
                className="w-full h-72 object-cover"
              />
              <div className="p-4">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-xs bg-yellow-500 text-black font-bold px-2 py-1 rounded">
                    {film.type}
                  </span>
                  <span className="text-xs bg-red-500 text-white font-bold px-2 py-1 rounded">
                    {film.rating}
                  </span>
                </div>
                <h2 className="text-lg font-bold text-gray-800">{film.name}</h2>
                <div className="mt-4 flex justify-between items-center">
                  <a
                    href={film.trailer}
                    className="text-sm text-blue-500 underline"
                  >
                    Xem Trailer
                  </a>
                  <button className="bg-yellow-500 text-black font-bold px-4 py-2 rounded hover:bg-yellow-600">
                    Đặt vé
                  </button>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default Slider;
