import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import CardComp from "./CardComp/CardComp";

const filmProfiles = [
  {
    name: "Linh Miêu: Quỷ Nhập Tràng (T18)",
    img: "https://cinestar.com.vn/_next/image/?url=https%3A%2F%2Fapi-website.cinestar.com.vn%2Fmedia%2Fwysiwyg%2FPosters%2F11-2024%2Flinh-mieu-official.png&w=256&q=40",
    type: "2D",
    rating: "113",
    trailer: "https://example.com/trailer-linh-mieu",
    isShowing: true,
  },
  {
    name: "Cười Xuyên Biên Giới (T13) PD",
    img: "https://example.com/cuoi-xuyen-pd.jpg",
    type: "2D",
    rating: "113",
    trailer: "https://example.com/trailer-cuoi-xuyen",
    isShowing: true,
  },
  {
    name: "Cười Xuyên Biên Giới LT (T13)",
    img: "https://example.com/cuoi-xuyen-lt.jpg",
    type: "2D",
    rating: "113",
    trailer: "https://example.com/trailer-cuoi-xuyen-lt",
    isShowing: true,
  },
  {
    name: "Võ Sĩ Giác Đấu II (T18)",
    img: "https://example.com/vo-si.jpg",
    type: "2D",
    rating: "118",
    trailer: "https://example.com/trailer-vo-si",
    isShowing: true,
  },
];

const Slider = () => {
  return (
    <div className="bg-[#1C1C3A] py-8">
      <Swiper
        modules={[Navigation, Pagination]}
        navigation
        pagination={{ clickable: true }}
        spaceBetween={20}
        slidesPerView={3}
        breakpoints={{
          640: { slidesPerView: 1 },
          768: { slidesPerView: 2 },
          1024: { slidesPerView: 5 },
        }}
        className="container mx-auto"
      >
        {filmProfiles.map((film, index) => (
          <SwiperSlide key={index}>
             <CardComp key={film.name} {...film} />
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default Slider;
